mapper.update:
UPDATE tb_supplier
SET name=#{name}
WHERE id=#{id}

UPDATE tb_stock
SET number = #{stock.number}
WHERE id = #{stock.id} AND updatetime = #{stock.updatetime}

UPDATE tb_order
SET address_id = #{addressID}, updatetime = now()
WHERE id = #{orderID}

UPDATE tb_order
SET status = #{status}, updatetime = now()
WHERE id = #{orderID}

mapper.select:
SELECT *
FROM tb_order
WHERE user_id = #{userID} AND status = #{status}

SELECT id, order_id AS orderId, goods_id AS goodsId, number, price, updatetime
FROM tb_order_item
WHERE order_id = #{orderID}

SELECT id, address_id AS addressId, user_id AS userId, number, status, time, updatetime, price
FROM tb_order
WHERE id = #{orderID}

SELECT score
FROM tb_user
WHERE id = #{userID}

SELECT *
FROM tb_vip_level
WHERE score_low <= #{score} AND score_high >= #{score}
