mapper.select:
SELECT num
FROM tb_card
WHERE user_id = #{userId,jdbcType=INTEGER}
AND goods_id = #{goodsId,jdbcType=INTEGER}
LIMIT 1

mapper.update:
UPDATE tb_card
SET num = num + #{num,jdbcType=INTEGER}, updatetime = now()
WHERE user_id = #{userId,jdbcType=INTEGER}
AND goods_id = #{goodsId,jdbcType=INTEGER}

mapper.delete:
DELETE FROM tb_card
WHERE id IN

  #{cardId}

AND user_id = #{userId}
